// Ensure jQuery is loaded and the DOM is ready
$(document).ready(function() {

    // IMPORTANT:
    // 1. INSPECT YOUR JOGET LIST'S HTML IN BROWSER DEVELOPER TOOLS (F12).
    //    Find the exact selector for your list's table rows.
    //    Common Joget list table selector is '.dataList table tbody tr'.
    //    If yours is different, replace '.dataList table tbody tr' below.
    // 2. VERIFY THE COLUMN INDEX FOR "Approval Status".
    //    In your image, "Approval Status" is the 3rd column (ID, Created By Name, Approval Status).
    //    So, its 0-indexed position is 2 (td:eq(2)).
    //    If your list columns change, you MUST update this index.

    var $listRows = $('.dataList table tbody tr');

    $listRows.each(function() {
        var $row = $(this);
        // Get the text content of the 'Approval Status' column
        // Adjust 'td:eq(2)' if the column index changes in your list
        var approvalStatusCellText = $row.find('td:eq(2)').text().trim();

        if (approvalStatusCellText === 'Approved') {
            $row.addClass('approved-row');
        } else if (approvalStatusCellText === 'Rejected') {
            $row.addClass('rejected-row');
        }
        // You can add more conditions here if you have other statuses
        // else if (approvalStatusCellText === 'Pending') {
        //     $row.addClass('pending-row');
        // }
    });
});
