document.addEventListener('DOMContentLoaded', function() {
    // Find elements using their classes or IDs. You'll need to inspect your Joget page
    // to get the exact selectors.
    const sidebar = document.querySelector('.sidebar-wrapper'); // Replace with actual sidebar selector
    const mainContent = document.querySelector('.content-wrapper'); // Replace with actual content wrapper selector
    const tableHeader = document.querySelector('.data-grid table thead th'); // Table header cells
    const tableRows = document.querySelectorAll('.data-grid table tbody tr'); // All table body rows
    const editButtons = document.querySelectorAll('.data-grid .btn.edit-btn'); // All edit buttons

    if (sidebar) {
        sidebar.style.backgroundColor = '#367fa9'; // Darker blue
        sidebar.style.color = '#ffffff'; // White text
    }

    if (mainContent) {
        mainContent.style.backgroundColor = '#e6f7ff'; // Lighter blue
    }

    if (tableHeader) {
        tableHeader.style.backgroundColor = '#a3d9ff';
        tableHeader.style.color = '#333';
    }

    if (tableRows) {
        tableRows.forEach((row, index) => {
            if (index % 2 === 0) { // Even rows
                row.style.backgroundColor = '#ffffff';
            } else { // Odd rows
                row.style.backgroundColor = '#f0faff';
            }
            row.addEventListener('mouseover', function() {
                this.style.backgroundColor = '#cceeff'; // Hover effect
            });
            row.addEventListener('mouseout', function() {
                if (index % 2 === 0) {
                    this.style.backgroundColor = '#ffffff';
                } else {
                    this.style.backgroundColor = '#f0faff';
                }
            });
        });
    }

    if (editButtons) {
        editButtons.forEach(button => {
            button.style.backgroundColor = '#007bff';
            button.style.color = '#ffffff';
            button.style.borderColor = '#007bff';

            button.addEventListener('mouseover', function() {
                this.style.backgroundColor = '#0056b3';
                this.style.borderColor = '#0056b3';
            });
            button.addEventListener('mouseout', function() {
                this.style.backgroundColor = '#007bff';
                this.style.borderColor = '#007bff';
            });
        });
    }
});

document.addEventListener('DOMContentLoaded', function() {
    const style = document.createElement('style');
    style.id = 'joget-custom-theme-styles'; // Give it an ID to avoid duplicate injections

    const css = `
        /* General Body/Background */
        body {
            background-color: #f0f8ff !important; /* Alice Blue */
        }

        /* Sidebar (Left Navigation) */
        .sidebar-wrapper { /* Adjust selector based on inspection */
            background-color: #367fa9 !important; /* Darker blue */
            color: #ffffff !important;
        }

        .sidebar-wrapper .nav-item .nav-link {
            color: #ffffff !important;
        }

        .sidebar-wrapper .nav-item.active > .nav-link,
        .sidebar-wrapper .nav-item:hover > .nav-link {
            background-color: #2a6187 !important; /* Slightly darker on hover/active */
        }

        /* Main Content Area */
        .content-wrapper, .content-container { /* Adjust selector based on inspection */
            background-color: #e6f7ff !important; /* Lighter blue */
        }

        /* List Table Header */
        .data-grid table thead th {
            background-color: #a3d9ff !important; /* Moderate blue */
            color: #333 !important;
        }

        /* List Table Rows */
        .data-grid table tbody tr {
            background-color: #ffffff !important; /* Default row color */
        }

        .data-grid table tbody tr:nth-child(even) {
            background-color: #f0faff !important; /* Slightly different for even rows */
        }

        .data-grid table tbody tr:hover {
            background-color: #cceeff !important; /* Hover effect */
        }

        /* Edit Buttons */
        .data-grid .btn.edit-btn { /* Adjust selector */
            background-color: #007bff !important;
            color: #ffffff !important;
            border-color: #007bff !important;
        }

        .data-grid .btn.edit-btn:hover {
            background-color: #0056b3 !important;
            border-color: #0056b3 !important;
        }
    `;

    // Only inject if it hasn't been injected before (e.g., on page reload)
    if (!document.getElementById('joget-custom-theme-styles')) {
        style.appendChild(document.createTextNode(css));
        document.head.appendChild(style);
    }
});
