// Ensure the code runs after the DOM is fully loaded
$(document).ready(function() {

    // --- Option 1: Change background color and add a border to the .table-wrapper ---
    // This will likely affect the area immediately around the table, creating a "side" effect.
    $('.table-wrapper').css({
        'background-color': '#f8f8f8', // A very light gray background for the wrapper
        'border': '2px solid #cccccc',  // A light gray border around the wrapper
        'padding': '10px'               // Add some padding so the background/border is visible around the table
    });

    // Optional: To ensure the table itself doesn't have its own conflicting background
    $('.table-wrapper table').css('background-color', 'transparent');


    // --- Option 2: Change the border of the <table> element itself ---
    // This will directly put a border around the table's rows and columns.
    $('table.xrounded_shadowed.responsivetable').css({
        'border': '2px solid #555555', // Dark gray border
        'border-collapse': 'collapse'  // Essential for table borders to behave nicely
    });

    // Optional: To ensure individual cells also inherit or have their own border for a consistent look
    $('table.xrounded_shadowed.responsivetable th, table.xrounded_shadowed.responsivetable td').css({
        'border': '1px solid #dddddd' // Lighter border for cells
    });


    // --- Option 3: Change the background of the main list container ---
    // If the "side" you refer to is the entire area where the list is displayed,
    // you might need to target the div with data-cbuilder-id="approvedBookinglist".
    $('div[data-cbuilder-id="approvedBookinglist"]').css({
        'background-color': '#e0e0e0', // A slightly darker gray background for the overall list area
        'padding': '20px',             // Add padding to make the background visible around the table wrapper
        'border': '1px solid #999999'  // Optional: add a border to this outer container
    });

});
