document.addEventListener('DOMContentLoaded', function() {
    // Find elements using their classes or IDs. You'll need to inspect your Joget page
    // to get the exact selectors.
    const sidebar = document.querySelector('.sidebar-wrapper'); // Replace with actual sidebar selector
    const mainContent = document.querySelector('.content-wrapper'); // Replace with actual content wrapper selector
    const tableHeader = document.querySelector('.data-grid table thead th'); // Table header cells
    const tableRows = document.querySelectorAll('.data-grid table tbody tr'); // All table body rows
    const editButtons = document.querySelectorAll('.data-grid .btn.edit-btn'); // All edit buttons

    if (sidebar) {
        sidebar.style.backgroundColor = '#367fa9'; // Darker blue
        sidebar.style.color = '#ffffff'; // White text
    }

    if (mainContent) {
        mainContent.style.backgroundColor = '#e6f7ff'; // Lighter blue
    }

    if (tableHeader) {
        tableHeader.style.backgroundColor = '#a3d9ff';
        tableHeader.style.color = '#333';
    }

    if (tableRows) {
        tableRows.forEach((row, index) => {
            if (index % 2 === 0) { // Even rows
                row.style.backgroundColor = '#ffffff';
            } else { // Odd rows
                row.style.backgroundColor = '#f0faff';
            }
            row.addEventListener('mouseover', function() {
                this.style.backgroundColor = '#cceeff'; // Hover effect
            });
            row.addEventListener('mouseout', function() {
                if (index % 2 === 0) {
                    this.style.backgroundColor = '#ffffff';
                } else {
                    this.style.backgroundColor = '#f0faff';
                }
            });
        });
    }

    if (editButtons) {
        editButtons.forEach(button => {
            button.style.backgroundColor = '#007bff';
            button.style.color = '#ffffff';
            button.style.borderColor = '#007bff';

            button.addEventListener('mouseover', function() {
                this.style.backgroundColor = '#0056b3';
                this.style.borderColor = '#0056b3';
            });
            button.addEventListener('mouseout', function() {
                this.style.backgroundColor = '#007bff';
                this.style.borderColor = '#007bff';
            });
        });
    }
});
