document.addEventListener('DOMContentLoaded', function() {
    const style = document.createElement('style');
    style.id = 'joget-custom-theme-styles'; // Give it an ID to avoid duplicate injections

    const css = `
        /* General Body/Background */
        body {
            background-color: #f0f8ff !important; /* Alice Blue */
        }

        /* Sidebar (Left Navigation) */
        .sidebar-wrapper { /* Adjust selector based on inspection */
            background-color: #367fa9 !important; /* Darker blue */
            color: #ffffff !important;
        }

        .sidebar-wrapper .nav-item .nav-link {
            color: #ffffff !important;
        }

        .sidebar-wrapper .nav-item.active > .nav-link,
        .sidebar-wrapper .nav-item:hover > .nav-link {
            background-color: #2a6187 !important; /* Slightly darker on hover/active */
        }

        /* Main Content Area */
        .content-wrapper, .content-container { /* Adjust selector based on inspection */
            background-color: #e6f7ff !important; /* Lighter blue */
        }

        /* List Table Header */
        .data-grid table thead th {
            background-color: #a3d9ff !important; /* Moderate blue */
            color: #333 !important;
        }

        /* List Table Rows */
        .data-grid table tbody tr {
            background-color: #ffffff !important; /* Default row color */
        }

        .data-grid table tbody tr:nth-child(even) {
            background-color: #f0faff !important; /* Slightly different for even rows */
        }

        .data-grid table tbody tr:hover {
            background-color: #cceeff !important; /* Hover effect */
        }

        /* Edit Buttons */
        .data-grid .btn.edit-btn { /* Adjust selector */
            background-color: #007bff !important;
            color: #ffffff !important;
            border-color: #007bff !important;
        }

        .data-grid .btn.edit-btn:hover {
            background-color: #0056b3 !important;
            border-color: #0056b3 !important;
        }
    `;

    // Only inject if it hasn't been injected before (e.g., on page reload)
    if (!document.getElementById('joget-custom-theme-styles')) {
        style.appendChild(document.createTextNode(css));
        document.head.appendChild(style);
    }
});
